import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";
import classes from "./AvailableMeals.module.css";
import { useEffect } from "react";

const DUMMY_MEALS = [
  {
    id: "m1",
    name: "Sushi",
    description: "Finest fish and veggies",
    price: 22.99,
  },
  {
    id: "m2",
    name: "Schnitzel",
    description: "A german specialty!",
    price: 16.5,
  },
  {
    id: "m3",
    name: "Barbecue Burger",
    description: "American, raw, meaty",
    price: 12.99,
  },
  {
    id: "m4",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
  },
];

const AvailableMeals = () => {
  const mealsList = [];

  useEffect(() => {
    const fetchMeals = async () => {
      const response = await fetch(
        "https://react-meals-12cbb-default-rtdb.firebaseio.com/meals.json"
      );
      const responseData = await response.json();
      console.log(responseData);

      for (const mealId in responseData) {
        const mealOBJ = {
          id: mealId,
          name: responseData[mealId].name,
          description: responseData[mealId].description,
          price:responseData[mealId].price
        }
        mealsList.push(mealOBJ);
      }

    };

    fetchMeals();
  }, []);


  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
